import urllib.parse
import re

def decode_fully(text):
    """URLエンコードがなくなるまで完全にデコードする"""
    while "%" in text:
        decoded = urllib.parse.unquote(text)
        if decoded == text:
            break
        text = decoded
    return text

def analyze_yamato_line_url(url):
    """URLを解析し、URLパラメータのエンコードを維持しつつテキストをデコードする"""
    try:
        parsed = urllib.parse.urlparse(url)
        qs = urllib.parse.parse_qs(parsed.query)
        return_uri = qs.get('returnUri', [None])[0]
        
        parsed_2 = urllib.parse.urlparse(return_uri)
        qs_2 = urllib.parse.parse_qs(parsed_2.query)
        redirect_uri = qs_2.get('redirect_uri', [None])[0]

        parsed_3 = urllib.parse.urlparse(redirect_uri)
        qs_3 = urllib.parse.parse_qs(parsed_3.query)
        return_url = qs_3.get('returnUrl', [None])[0]

        if return_url:
            # ここがポイント！
            # 全体をデコードする前に「text=」の位置を探して、そこから後ろだけをデコードします
            if "text=" in return_url:
                # 1. 「text=」より前（URL部分）と後ろ（メッセージ部分）に分ける
                url_part, text_part = return_url.split("text=", 1)
                
                # 2. メッセージ部分だけを完全にデコードする
                decoded_text = decode_fully(text_part)
                
                # 3. LINEのゴミを掃除する
                cleaned_text = decoded_text.replace("&from=line_scheme", "").strip()
                
                # 4. URL部分はそのまま（%2Fなどが残った状態）に「text=」と「デコード後の日本語」を合体
                # ただし、url_partの最後にデコード前のURLが残っているので、
                # 必要なヤマトのURLだけを抽出するように整えます
                
                # --- 修正後 ---
                # url_partの中から、ヤマトのURLが始まる位置を探して、そこから後ろだけを抜き出す
                if "https://sp-send.kuronekoyamato.co.jp/" in url_part:
                    base_url = "https://sp-send.kuronekoyamato.co.jp/" + url_part.split("https://sp-send.kuronekoyamato.co.jp/")[1]
                    # さらに、もし末尾に余計な記号があればカットする
                    base_url = base_url.split("&")[0] if "&" in base_url else base_url
                else:
                    base_url = url_part # 見つからない場合はそのまま
                # 最終的な形： [デコードした日本語] + [元の形式を維持したURL]
                # ※「本物」の形式に合わせて組み立て
                
                # 日本語メッセージ部分からURLの案内文を抜き出す
                res_text = cleaned_text.replace("https://sp-send.kuronekoyamato.co.jp/", base_url)
                
                return res_text
    except Exception as e:
        return f"エラー: {e}"
    return "解析失敗"


